  const handleDelete = (id) => {
    const newBlogs = blogs.filter((blog) => blog.id !== id);
    setBlogs(newBlogs);
  };

  return (
    <div className="home">
      {error && <div> {error}</div>}
      {isPending && <div>Loading...</div>}
      {blogs && (
        <BlogList blogs={data} title="All Blogs" handleDelete={handleDelete} />
      )}
    </div>
  );
};

export default Home;
