import React, { Component } from "react";
import "./products.css";
import { WithSoundCloudAudio } from "react-soundplayer/src/components";
import {
  PlayButton,
  Timer,
  VolumeControl,
} from "react-soundplayer/src/components";
import PropTypes from "prop-types";

export default class Product extends Component {
  constructor(props) {
    super(props);

    //console.log("Constructor - Product");

    this.state = {
      product: this.props.product,
    };

    const { track } = this.props.product;
  }

  render() {
    return (
      <div className="product__content">
        <div className="product__cards">
          <div className="product__card">
            <div className="text-muted">
              {/* delete button starts */}
              <span
                className="pull-right hand-icon"
                onClick={() => {
                  this.props.onDelete(this.state.product);
                }}
              >
                <i className="fa fa-times"></i>
              </span>
              {/* delete button ends */}
            </div>

            <div className="p1 mb3 mt1 flex flex-center bg-darken-1 red rounded">
              <PlayButton
                className="flex-none h4 button button-transparent button-grow rounded"
                {...this.props}
              />
              <VolumeControl
                className="flex flex-center mr2"
                buttonClassName="flex-none h4 button button-transparent button-grow rounded"
                {...this.props}
              />
              <h2 className="h5 nowrap caps flex-auto m0">
                {track ? track.title : "Loading..."}
              </h2>
              <Timer
                className="h6 mr1"
                duration={track ? track.duration / 1000 : 0}
                {...this.props}
              />
            </div>

            <h5 className="pt-2 border-top">
              {this.state.product.productName}
            </h5>

            <div>$ {this.state.product.price}</div>
          </div>
          {/* card body ends here */}

          <div className="card-footer">
            <div className="float-left">
              <span className="badge">{this.state.product.quantity}</span>

              <div className="btn-group">
                <button
                  className="btn btn-outline-success"
                  onClick={() => {
                    this.props.onIncrement(this.state.product, 10);
                  }}
                >
                  +
                </button>

                <button
                  className="btn btn-outline-success"
                  onClick={() => {
                    this.props.onDecrement(this.state.product, 0);
                  }}
                >
                  -
                </button>
              </div>
            </div>
            {/* float-left ends here */}

            <div className="float-right">{this.props.children}</div>
          </div>
          {/* card-footer ends here */}
        </div>
      </div>
    );
  }

  componentDidMount() {
    //console.log("componentDidMount - Product");
  }

  componentDidUpdate() {
    //console.log("componentDidUpdate - Product");
  }

  //Executes when the current instance of current component is being deleted from memory
  componentWillUnmount() {
    //console.log("componentWillUnmount - Product");
  }
}
