import React, { useState, useEffect } from "react";
import Navbar from "./Components/Navbar/Navbar";
import PrimarySearchAppBar from "./Components/Navbar/Navebar1";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Header from "./Components/Header/Header";
import About from "./Components/About/About";
import Login from "./Components/Login/Login";
import Products from "./Components/Products/Products";
import Cart from "./Components/Cart/Cart";

function App() {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState("");
  const [cart, setCart] = useState({});

  const fetchProducts = async () => {
    const data = await fetch("http://localhost:5000/products", {
      method: "GET",
    });
    var body = await data.json();
    if (body.length > 0) {
      setProducts({ products: body });
    } else {
      setError("No items");
    }
  };
  const fetchCart = async () => {
    setCart(await products);
  };

  const handleAddToCart = async (productId, quantity) => {
    const item = await cart.add(productId, quantity);

    setCart(item.cart);
  };

  const handleUpdateCartQty = async (lineItemId, quantity) => {
    const response = await cart.update(lineItemId, { quantity });

    setCart(response.cart);
  };

  const handleRemoveFromCart = async (lineItemId) => {
    const response = await cart.remove(lineItemId);

    setCart(response.cart);
  };

  const handleEmptyCart = async () => {
    const response = await cart.empty();

    setCart(response.cart);
  };

  const refreshCart = async () => {
    const newCart = await cart.refresh();

    setCart(newCart);
  };

  useEffect(() => {
    fetchProducts();
    fetchCart();
  }, []);

  return (
    <div>
      <Router>
        <Navbar />
        <Switch>
          <Route exact path="/">
            <Header />
          </Route>
          <Route exact path="/login">
            <Login />
          </Route>
          <Route exact path="/products">
            <Products onAddToCart={handleAddToCart} handleUpdateCartQty />
          </Route>
          <Route exact path="/cart">
            <Cart
              cart={cart}
              onUpdateCartQty={handleUpdateCartQty}
              onRemoveFromCart={handleRemoveFromCart}
              onEmptyCart={handleEmptyCart}
            />
          </Route>
          <Route exact path="/about">
            <About />
          </Route>
        </Switch>
      </Router>
    </div>
  );
}

export default App;

